sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var table = current.getTableName();

	var dt = new devTrackServer();
	var tables = dt.getM2MTables(table ,'x_417679_devtrack_sn_module');
	var response = dt.updateMainM2M(tables, current.sys_id, current.main_module);

	msg = 'Affected Modules list has been updated with the new main module.';

	//if any main rel flags were removed
	if(response.some(function(r){return r.action=="update_remove"?true:false;})) {
		msg += ' The previous main module relationship will not be removed from the Affected Modules list.';
	}
	gs.addInfoMessage(msg);


})(current, previous);
