sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if(isLoading || newValue=='') return;

	var ga = new GlideAjax('devTrackServer');
    ga.addParam('sysparm_name', 'getRaisedInfo');
	ga.addParam('sysparm_record', newValue);
    ga.getXML(function(response){
		var r = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
		for(var f in r) {
			jslog(f+": "+r[f]);
			var old = g_form.getDisplayBox(f)==null ? g_form.getValue(f) : g_form.getDisplayBox(f).value;
			g_form.setValue(f, r[f]);
			g_form.flash(f,'orange', 0);
			if(r[f]!=old) g_form.showFieldMsg(f,'This value has been changed from \''+old+'\' because Raised From was updated.');
		}

	});


}
